
CREATE TABLE characterclass (
    Id SERIAL primary key,
    Classification VARCHAR(50),
    ClassName VARCHAR(100),
    HitDie INT,
    ManaDie INT,
    ProficiencyCount INT,
    MagicBooks INT,
    Cantrips INT,
    Chances INT,
    Description TEXT,
    Starter VARCHAR(3),
    SpellCastingModifier VARCHAR(20),
    StatFavor1 VARCHAR(10),
    StatFavor2 VARCHAR(10),
    ClassSpecific TEXT,
    LanguageCount INT,
    ProficiencyStart VARCHAR(50),
    VeteranTag VARCHAR(50)
);



CREATE TABLE characterrace (
	Id SERIAL primary key,
    Campaign VARCHAR(60),
    SubType VARCHAR(60),
    raceName VARCHAR(60),
    raceDescription TEXT,
    Starter VARCHAR(3),
    Special VARCHAR(150),
    Str INT,
    Dex INT,
    Con INT,
    Wis INT,
    raceInt INT,
    Cha INT,
    Pick VARCHAR(4),
    BonusMana INT,
    AddOrMultMana VARCHAR(5),
    Speed INT NULL,
    Languages VARCHAR(20)
);



CREATE TABLE racevariant (
    Id SERIAL primary Key,
    Race VARCHAR(50),
    Variant VARCHAR(50),
    Str INT,
    Dex INT,
    Con INT,
    raceInt INT,
    Wis INT,
    Cha INT,
    Pick VARCHAR(50),
    ManaBonus INT,
    AddOrMultMana VARCHAR(10),
    SpeedOverride INT
);



create table player(
	Id SERIAL primary Key,
	playerName VARCHAR(50),
	veteran BOOL,
	username VARCHAR(25),
	pin INT
	
	
);

create table characterstats(
	Id SERIAL primary Key,
	baseStr INT,
    baseDex INT,
    baseCon INT,
    baseraceInt INT,
    baseWis INT,
    baseCha INT,
    Health INT,
    Mana INT,
    charlevel INT
);

create table modifiedstats(
	Id SERIAL primary Key,
	modStr INT,
    modDex INT,
    modCon INT,
    modraceInt INT,
    modWis INT,
    modCha INT
);

create table playercharacter(
	Id SERIAL primary Key,
	PlayerId INT,
	charName VARCHAR(80),
	raceId INT NULL,
	racevariantId INT null,
	classId INT null,
	statId INT null,
	modstatId INT null,
	FOREIGN KEY (modstatId) REFERENCES modifiedstats(Id),
	FOREIGN KEY (PlayerId) REFERENCES player(Id),
    FOREIGN KEY (raceId) REFERENCES characterrace(Id),
    FOREIGN KEY (racevariantId) REFERENCES racevariant(Id),
    FOREIGN KEY (classId) REFERENCES characterclass(Id),
    FOREIGN KEY (statId) REFERENCES characterstats(Id)
	
);






